import React, {
    Component
} from "react";
import moment from 'moment';
import Moment from 'react-moment'; //bootstrap import
import {
    Dropdown,
    Table
} from 'react-bootstrap'; //import './App.css';

// react bootstrap table
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';

import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';


class demoTimesheet extends Component {
    constructor() {
        super();
        this.state = {
            monthObj: {
                January: 0,
                Febraury: 1,
                March: 2,
                April: 3,
                May: 4,
                June: 5,
                July: 6,
                August: 7,
                September: 8,
                October: 9,
                November: 10,
                December: 11
            },
            weekObj: [],
            tableObj: [],
            dayObj: [],
            startDate: "",
            endDate: "",
            optionItems: [],
            value:'',
            // start : moment([2020, 8 - 1]).startOf('month'), 
            // end : moment([2020, 8 - 1]).endOf('month'), 
            // nrofWeeks : moment.duration(end - start).weeks()

            products : [
                    {
                        "id": "1000",
                        "code": "f230fh0g3",
                        "name": "Bamboo Watch",
                        "description": "Product Description",
                        "image": "bamboo-watch.jpg",
                        "price": 65,
                        "category": "Accessories",
                        "quantity": 24,
                        "inventoryStatus": "INSTOCK",
                        "rating": 5
                    },
                    {
                        "id": "1001",
                        "code": "nvklal433",
                        "name": "Black Watch",
                        "description": "Product Description",
                        "image": "black-watch.jpg",
                        "price": 72,
                        "category": "Accessories",
                        "quantity": 61,
                        "inventoryStatus": "INSTOCK",
                        "rating": 4
                    },
                    {
                        "id": "1002",
                        "code": "zz21cz3c1",
                        "name": "Blue Band",
                        "description": "Product Description",
                        "image": "blue-band.jpg",
                        "price": 79,
                        "category": "Fitness",
                        "quantity": 2,
                        "inventoryStatus": "LOWSTOCK",
                        "rating": 3
                    },
                    {
                        "id": "1003",
                        "code": "244wgerg2",
                        "name": "Blue T-Shirt",
                        "description": "Product Description",
                        "image": "blue-t-shirt.jpg",
                        "price": 29,
                        "category": "Clothing",
                        "quantity": 25,
                        "inventoryStatus": "INSTOCK",
                        "rating": 5
                    },
                    {
                        "id": "1004",
                        "code": "h456wer53",
                        "name": "Bracelet",
                        "description": "Product Description",
                        "image": "bracelet.jpg",
                        "price": 15,
                        "category": "Accessories",
                        "quantity": 73,
                        "inventoryStatus": "INSTOCK",
                        "rating": 4
                    },
                    {
                        "id": "1005",
                        "code": "av2231fwg",
                        "name": "Brown Purse",
                        "description": "Product Description",
                        "image": "brown-purse.jpg",
                        "price": 120,
                        "category": "Accessories",
                        "quantity": 0,
                        "inventoryStatus": "OUTOFSTOCK",
                        "rating": 4
                    },
                    {
                        "id": "1006",
                        "code": "bib36pfvm",
                        "name": "Chakra Bracelet",
                        "description": "Product Description",
                        "image": "chakra-bracelet.jpg",
                        "price": 32,
                        "category": "Accessories",
                        "quantity": 5,
                        "inventoryStatus": "LOWSTOCK",
                        "rating": 3
                    },
                    {
                        "id": "1007",
                        "code": "mbvjkgip5",
                        "name": "Galaxy Earrings",
                        "description": "Product Description",
                        "image": "galaxy-earrings.jpg",
                        "price": 34,
                        "category": "Accessories",
                        "quantity": 23,
                        "inventoryStatus": "INSTOCK",
                        "rating": 5
                    },
                    {
                        "id": "1008",
                        "code": "vbb124btr",
                        "name": "Game Controller",
                        "description": "Product Description",
                        "image": "game-controller.jpg",
                        "price": 99,
                        "category": "Electronics",
                        "quantity": 2,
                        "inventoryStatus": "LOWSTOCK",
                        "rating": 4
                    },
                    {
                        "id": "1009",
                        "code": "cm230f032",
                        "name": "Gaming Set",
                        "description": "Product Description",
                        "image": "gaming-set.jpg",
                        "price": 299,
                        "category": "Electronics",
                        "quantity": 63,
                        "inventoryStatus": "INSTOCK",
                        "rating": 3
                    }
                ]
            
          
        };

        this.handleChange = this.handleChange.bind(this);
        this.keyPress = this.keyPress.bind(this);
        //let weeks = []; 
        this.state.optionItems = Object.keys(this.state.monthObj).map(item => < Dropdown.Item onSelect={
            () => this.onTargetSelect(this.state.monthObj[item])
        }
            eventKey={
                this.state.monthObj.item
            } > {
                item
            } </Dropdown.Item>); console.log('this.props', this.props);
    }


    onTargetSelect = function (item) {
        this.state.weekObj = this.state.weekObj ? [] : '';
        let newDate = moment().year() + '-' + (item + 1) + '-01';
        let weeks = [];
        this.state.startDate = moment('' + newDate).startOf('week').format('YYYY-MM-DD');
        this.state.endDate = moment('' + newDate).endOf('month').format('YYYY-MM-DD');
        console.log('weeksaaaa:', this.state.startDate, this.state.endDate);
        while (this.state.startDate <= this.state.endDate) {
            weeks.push(<Dropdown.Item onSelect={(event) => this.selectedWeek(event)}
                eventKey={this.state.startDate}>{this.state.startDate}</Dropdown.Item >);
            this.state.startDate = moment(this.state.startDate).add(7, 'days').format('YYYY-MM-DD');
        }
        console.log(weeks)
        this.setState({
            weekObj: weeks
        });
    }
    selectedWeek = function (week) {
        console.log('ll', week,this.state.columns);
        let row = {
            dataField: '',
            text: ''
        };
        let data = week
        let i = 1;
        let days = [];
        //days.push(data);
        row.dataField = ''+data;
        row.text = ''+data; 
        this.state.columns[0] = row;
        while (i <= 6) {
            let c=1
            let daysString = moment(data).add(1, 'days').format('YYYY-MM-DD');
            // days.push(daysString);
            row.dataField = ''+daysString;
            row.text = ''+daysString; 
            this.state.columns[c] = row;
            data = moment(data).add(1, 'days').format('YYYY-MM-DD');
            i++;
            c++;
        }


        this.setState({
            dayObj: days
        });

        console.log('days', days, this.state.dayObj);
    }
    handleSelect = (e) => {
        console.log(e);
        // setValue(e) 
    }
    onKeyUpValue=(e)=> {
        console.log("ee",e)
        if (e.key === 'Enter') {
          console.log('do validate',e);
        }
      }

      handleChange(e) {
        this.setState({ value: e.target.value });
     }
  
     keyPress(e,d){
        console.log(e,d)
        if(e.keyCode == 13){
           console.log('value', e.target.value);
           // put the login here
        }
     }

    render() {
        return (<div className="demoTimesheet" >
            <Dropdown>
                <Dropdown.Toggle variant="success"
                    id="select-month" > Select Month
            </Dropdown.Toggle>
                <Dropdown.Menu > {
                    this.state.optionItems
                } </Dropdown.Menu>
            </Dropdown>
            <Dropdown>
                <Dropdown.Toggle variant="success"
                    id="select-week" > Select Week
            </Dropdown.Toggle >
                <Dropdown.Menu> {
                    this.state.weekObj
                } </Dropdown.Menu> </Dropdown >


<DataTable value={this.state.products} editMode="cell" className="editable-cells-table">
                        <Column field="code" header="Code"></Column>
                        <Column field="name" header="Name"></Column>
                        <Column field="category" header="Category"></Column>
                        <Column field="quantity" header="Quantity"></Column>
                    </DataTable>
            
            <Table bordered hover className="timesheet-table" size="sm">
                <thead >


                    <tr >
                        {this.state.dayObj.map((day, index) => (
                            <th key={index}>{day}</th>
                        ))}
                    </tr>
                    <tr>
                        <th> Sunday</th >
                        <th> Monday</th >
                        < th > Tuesday </th>
                        <th> Wednesday</th >
                        < th > Thursday </th>
                        <th> Friday</th >
                        < th > Saturday </th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td contenteditable='false'></td >
                        <td contenteditable='true' data-title="12-12-2020"value={this.state.value} onKeyDown={this.keyPress} onChange={this.handleChange}>
</td>
                        <td contenteditable='true'> </td >
                        <td contenteditable='true'> </td>
                        <td contenteditable='true'> </td >
                        <td contenteditable='true'> </td>
                        <td contenteditable='false'></td>
                    </tr >
                </tbody>
            </Table >
        </div>
        );
    }
}
export default demoTimesheet;
