import React, { Component } from "react";
import moment from 'moment';
import Moment from 'react-moment';

//bootstrap import
import { Dropdown, Table } from 'react-bootstrap';


//import './App.css';

class demoTimesheet extends Component {

    constructor() {
        super();
        this.state = {
            monthObj : {
                January: 0,
                Febraury: 1,
                March: 2,
                April: 3,
                May: 4,
                June: 5,
                July: 6,
                August: 7,
                September: 8,
                October: 9,
                November: 10,
                December: 11
            },
            weekObj:[],
            tableObj :[],
            dayObj :[],
            startDate:"",
            endDate:"",

            
            optionItems : []
            //   start : moment([2020, 8 - 1]).startOf('month'),
            //  end : moment([2020, 8 - 1]).endOf('month'),

            //  nrofWeeks : moment.duration(end - start).weeks()
        };
        console.log('this.props', this.props);

    }



  


render(){

    this.state.startDate = moment('2020-01-01').startOf('week').format('YYYY-MM-DD');
    this.state.endDate = moment(new Date()).startOf('week').format('YYYY-MM-DD');
    
    const weeks = [];
    while ( this.state.startDate <= this.state.endDate) {
      weeks.push( this.state.startDate);
       this.state.startDate = moment( this.state.startDate).add(7, 'days').format('YYYY-MM-DD');
    }
    
    console.log('weeks:', weeks);

    this.state.optionItems = Object.keys(this.monthObj).map(item => <Dropdown.Item eventKey={this.state.monthObj.item} >{item}</Dropdown.Item>);
return (
    <div>
        <div>
            <select id="lang" onChange={this.change} value={this.state.value}>
                <option value="select">Select</option>
                <option value="Java">Java</option>
                <option value="C++">C++</option>
            </select>
            <p></p>
            <p>{this.state.value}</p>
        </div>
        <Dropdown  >
            <Dropdown.Toggle variant="success" id="select-month">
                Select Month
                </Dropdown.Toggle>
            <Dropdown.Menu >
            {this.state.optionItems}
            </Dropdown.Menu>
        </Dropdown>
        <Dropdown  >
            <Dropdown.Toggle variant="success" id="select-week">
                Select Week
                </Dropdown.Toggle>
            <Dropdown.Menu >
              
            </Dropdown.Menu>
        </Dropdown>
        <Table>
            <thead>
                <tr>
                   
                </tr>
                <tr>
                  
                </tr>
            </thead>
            <tbody>
                <tr>

                </tr>
            </tbody>
        </Table>


    </div>
);
}
}
export default demoTimesheet;
