import React from 'react';
import moment from 'moment';
import Moment from 'react-moment';

//bootstrap import
import {Dropdown}  from 'react-bootstrap';


//import './App.css';


function demoTimesheet() {


    let monthObj = {
        January: 0,
        Febraury: 1,
        March: 2,
        April: 3,
        May: 4,
        June: 5,
        July: 6,
        August: 7,
        September: 8,
        October: 9,
        November: 10,
        December: 11
    };
    let weekObj = [];
    let optionItems = Object.keys(monthObj).map(item => <Dropdown.Item eventKey={monthObj.item}>{item}</Dropdown.Item>);

    //moment js 

    var start = moment([2020, 8 - 1]).startOf('month');
    var end = moment([2020, 8 - 1]).endOf('month');

    var nrofWeeks = moment.duration(end - start).weeks();

    

    

    // // Clone the value before .endOf()
    // var endDate = moment(startDate).endOf('week');
    // var endDate2 = moment(startDate).endOf('month');
    // console.log('starting at', start.format());
    // console.log('ending at', end.format());
    // console.log('number of weeks', nrofWeeks);
    // // just for demonstration:
    // console.log(startDate.format("DD-MM-YYYY"));
    // console.log(endDate2.format("DD-MM-YYYY"));
    // console.log(endDate.add(7, 'days').format("DD-MM-YYYY"));

    // mommet js - end

    const handleSelect=(e:eventKey)=>{
        console.log(e);
    }

    // populate 4 weeks for selected month
    var startDate = moment([2020, 8 - 1]);// get month and year
    const sDate = startDate.startOf('month');
    var week1 = sDate.format("DD-MM-YYYY");
   // weekObj.push(week1);
    for(let i=0;i<=nrofWeeks;i++){
        console.log('b4',sDate.format("DD-MM-YYYY"));
        weekObj.push(sDate.add(1*i, 'weeks').format("DD-MM-YYYY") + '-' + sDate.add(6, 'days').format("DD-MM-YYYY"));
        console.log('a4',sDate.format("DD-MM-YYYY"));
    }

    console.log('weekObj',weekObj , nrofWeeks);
    // var week2 = sDate.add(1, 'weeks').format("DD-MM-YYYY");
    // var week3 = sDate.add(1, 'weeks').format("DD-MM-YYYY");
    // var week4 = sDate.add(1, 'weeks').format("DD-MM-YYYY");

    //console.log(week1, week2, week3, week4);



    return (
        <div>
            <Dropdown onSelect={handleSelect} >
                <Dropdown.Toggle variant="success" id="dropdown-basic">
                   Select Month
  </Dropdown.Toggle>

                <Dropdown.Menu >
                {optionItems}
                </Dropdown.Menu>
            </Dropdown>
         
        </div>
    );
}

export default demoTimesheet;
