import React from 'react';
import moment from 'moment';
import Moment from 'react-moment';

//bootstrap import
import { Dropdown ,Table} from 'react-bootstrap';


//import './App.css';

function demoTimesheet() {


    let monthObj = {
        January: 0,
        Febraury: 1,
        March: 2,
        April: 3,
        May: 4,
        June: 5,
        July: 6,
        August: 7,
        September: 8,
        October: 9,
        November: 10,
        December: 11
    };
    let weekObj = [];
    let tableObj = [];
    let dayObj = [];
    let optionItems = Object.keys(monthObj).map(item => <Dropdown.Item eventKey={monthObj.item} onClick={this.onClickHandler}>{item}</Dropdown.Item>);


  
    onClickHandler = event => {
        console.log('event',event);
        const value = event.target.innerHTML;
        this.setState({ value })
      }

    //moment js 

    var start = moment([2020, 8 - 1]).startOf('month');
    var end = moment([2020, 8 - 1]).endOf('month');

    var nrofWeeks = moment.duration(end - start).weeks();





    // // Clone the value before .endOf()
    // var endDate = moment(startDate).endOf('week');
    // var endDate2 = moment(startDate).endOf('month');
    // console.log('starting at', start.format());
    // console.log('ending at', end.format());
    // console.log('number of weeks', nrofWeeks);
    // // just for demonstration:
    // console.log(startDate.format("DD-MM-YYYY"));
    // console.log(endDate2.format("DD-MM-YYYY"));
    // console.log(endDate.add(7, 'days').format("DD-MM-YYYY"));

    // mommet js - end

    const handleSelect = (e) => {
        console.log(e);
    }

    

    // selectWeekRange(date){
    //     console.log(date);
    // }

    // populate 4 weeks for selected month
    var startDate = moment([2020, 8 - 1]);// get month and year
    const sDate = startDate.startOf('month');
    var week1 = sDate.format("DD-MM-YYYY");
    // weekObj.push(week1);
    for (let i = 0; i <= nrofWeeks; i++) {
        console.log('b4', sDate.format("DD-MM-YYYY"));
        weekObj.push(<Dropdown.Item>{sDate.add(i !== 0 ? 1 : 0, 'days').format("DD-MM-YYYY") + ' - ' + sDate.add(6, 'days').format("DD-MM-YYYY")}</Dropdown.Item>);
       tableObj.push(<th>{sDate.add(i !== 0 ? 1 : 0, 'days').format("DD-MM-YYYY") + ' - ' + sDate.add(6, 'days').format("DD-MM-YYYY")}</th>);
        dayObj.push(<th>{sDate.add(i !== 0 ? 1 : 0, 'days').format("dddd") + ' - ' + sDate.add(6, 'days').format("dddd")}</th>);
        console.log('a4', sDate.format("DD-MM-YYYY"));
    }

    console.log('weekObj', weekObj, nrofWeeks);
    // var week2 = sDate.add(1, 'weeks').format("DD-MM-YYYY");
    // var week3 = sDate.add(1, 'weeks').format("DD-MM-YYYY");
    // var week4 = sDate.add(1, 'weeks').format("DD-MM-YYYY");

    //console.log(week1, week2, week3, week4);



    return (
        <div>
            <Dropdown onSelect={handleSelect} >
                <Dropdown.Toggle variant="success" id="select-month">
                    Select Month
                </Dropdown.Toggle>
                <Dropdown.Menu >
                    {optionItems}
                </Dropdown.Menu>
            </Dropdown>
            <Dropdown  >
                <Dropdown.Toggle variant="success" id="select-week">
                    Select Week
                </Dropdown.Toggle>
                <Dropdown.Menu >
                    {weekObj}
                </Dropdown.Menu>
            </Dropdown>
            <Table>
                <thead>
                   <tr>
                   {tableObj}
                       </tr> 
                   <tr>
                   {dayObj}
                       </tr> 
                </thead>
                <tbody>
                    <tr>

                    </tr>
                </tbody>
            </Table>


        </div>
    );
}

export default demoTimesheet;
