import React, { Component } from "react";
import moment from 'moment';
import Moment from 'react-moment';

import PropTypes from "prop-types";
import {Button} from "react-bootstrap";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
class Dashboard extends Component {
  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
  };
render() {
    const { user } = this.props.auth;
    
  var start = moment('2016-10-05 12:00:00').utc().startOf('month');
  var end = moment('2016-10-05 12:00:00').utc().endOf('month');
  
  var nrofWeeks = moment.duration(end - start).weeks() + 1;
  
  var startDate = moment([2020, 8 - 1]);
  
      // Clone the value before .endOf()
      var endDate = moment(startDate).endOf('week');
  console.log('starting at', start.format());
  console.log('ending at', end.format());
  console.log('number of weeks', nrofWeeks);
    // just for demonstration:
    console.log(startDate.format("DD-MM-YYYY"));
    console.log(endDate.format("DD-MM-YYYY"));

return (
      <div className="timesheet-page" >
            <h4>
              {user.name.split(" ")[0]}!
              Here is your time sheet space
            </h4>
            <Button variant="primary" onClick={this.onLogoutClick}>Logout</Button>

            {/* timesheet section */}
            
      </div>
    );
  }
}
Dashboard.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  auth: state.auth
});
export default connect(
  mapStateToProps,
  { logoutUser }
)(Dashboard);
