import React from 'react';
import moment from 'moment';
import Moment from 'react-moment';


//import './App.css';


function demoTimesheet() {


    let monthObj ={
        January:1,
        Febraury:1,
        March:1,
        April:1,
        May:1,
        June:1,
        July:1


    }
    let optionItems = Object.keys(monthObj).map(item=><option key={item}>{item}</option>);

    //moment js 

    var start = moment('2016-10-05 12:00:00').utc().startOf('month');
    var end = moment('2016-10-05 12:00:00').utc().endOf('month');

    var nrofWeeks = moment.duration(end - start).weeks() + 1;

    var startDate = moment([2020, 8 - 1]);// get month

    var sDate = startDate.startOf('month')

    var week1 = sDate.format("DD-MM-YYYY");
    var week2 = sDate.add(1, 'weeks').format("DD-MM-YYYY");
    var week3 = sDate.add(1, 'weeks').format("DD-MM-YYYY");
    var week4 = sDate.add(1, 'weeks').format("DD-MM-YYYY");

    console.log(week1, week2, week3, week4);

    // Clone the value before .endOf()
    var endDate = moment(startDate).endOf('week');
    var endDate2 = moment(startDate).endOf('month');
    console.log('starting at', start.format());
    console.log('ending at', end.format());
    console.log('number of weeks', nrofWeeks);
    // just for demonstration:
    console.log(startDate.format("DD-MM-YYYY"));
    console.log(endDate2.format("DD-MM-YYYY"));
    console.log(endDate.add(7, 'days').format("DD-MM-YYYY"));

    // mommet js - end

    return (
        <div>
           <select>
              {optionItems}
               </select>
        </div>
    );
}

export default demoTimesheet;
